defprime_length(string):
    len_prime = 0
    if len(string) == 1:
        return 0
    for i in range(2, len(string)):
        if string.find('a', i) < 0:
            continue
        if string.find('a', i) == 0:
            return 0
        for j in range(i, len(string)):
            if string[j] == 'a' and j % i!= 0:
                break
        else:
            len_prime += 1
    return len_prime

