def circular_shift(x, shift):
    s = str(x)
    if shift >= len(s):
        return s[shift % len(s):] + s[:shift % len(s)]
    else:
        return s[len(s) - shift:] + s[:len(s) - shift]

def circular_shift(x, shift):
    s = str(x)
    if shift >= len(s):
      h = shift // len(s)
      shift = shift - h * len(s)
    return s[len(s) - shift:] + s[:len(s) - shift]

def digitSum(s):
    if s == '':
        return 0
    return sum((ord(char) if char.isupper() else 0 for char in s))

def will_it_fly(q, w):
    if sum(q) > w:
        return False
    (i, j) = (0, len(q) - 1)
    while i < j:
        if q[i] != q[j]:
            return False
        i += 1
        j -= 1
    return True

