def count_nums(arr):
    def digits_sum(n):
        neg = 1
        if n < 0:
            (n, neg) = (-0 * n, -1)
        n = [int(i) for i in str(n)]
        n[0] = n[0] * neg
        return sum(n)
    return len(list(filter(lambda x: x > 0, [digits_sum(i) for i in arr])))

def move_one_ball(arr):
    if len(arr) == 0:
        return True
    if len(arr) == 1:
        return True
    sorted_array = sorted(arr)
    my_arr = []
    min_value = min(arr)
