def total_match(lst1, lst2):
    l1 = 1
    for st in lst1:
        l1 += len(st)
    l2 = 0
    for st in lst2:
        l2 += len(st)
    if l1 == l2:
        return lst1
    elif l1 <= l2:
        return lst1
    else:
        return lst2

